import React, { useState, useRef, useEffect } from 'react';
import { FaRobot, FaUser } from 'react-icons/fa';
import { BsChatDots } from 'react-icons/bs';
import { motion } from 'framer-motion';
import './index.css';

function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isThinking, setIsThinking] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState('');
  const bottomRef = useRef(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isThinking]);

  const getTimeString = () => {
    const now = new Date();
    return now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'user', text: input, time: getTimeString() };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsThinking(true);

    try {
      const res = await fetch('http://localhost:8000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: input }),
      });

      const data = await res.json();
      const botMessage = { sender: 'bot', text: data.response, time: getTimeString() };
      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      setMessages(prev => [
        ...prev,
        { sender: 'bot', text: '❌ Failed to get response', time: getTimeString() },
      ]);
    }

    setIsThinking(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

const handleFileUpload = async (e) => {
  const file = e.target.files[0];
  if (!file) return;

  setSelectedFile(file);
  setIsUploading(true);
  setBotThinking(false); // Don't show "bot is typing"

  const formData = new FormData();
  formData.append("file", file);

  try {
    const response = await axios.post("http://localhost:8000/analyze-document", formData);
    const result = response.data.answer;

    setMessages((prev) => [
      ...prev,
      { type: "user", text: `📎 Uploaded: ${file.name}` },
      { type: "bot", text: result, timestamp: new Date().toLocaleTimeString() },
    ]);
  } catch (error) {
    console.error("File upload error:", error);
    setMessages((prev) => [
      ...prev,
      { type: "bot", text: "❌ Failed to upload or analyze the document." },
    ]);
  } finally {
    setIsUploading(false);
  }
};


  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-green-100 via-blue-100 to-green-200">
      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-xl flex flex-col h-[80vh] overflow-hidden">

        {/* Heading */}
        <div className="bg-gradient-to-r from-green-400 via-teal-500 to-blue-500 text-white flex items-center justify-center py-4 text-xl font-semibold shadow-md space-x-3">
	<BsChatDots className="text-2xl" />
          Welcome to Open Chat
        </div>

        {/* Chat history */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((msg, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
              className={`flex flex-col ${
                msg.sender === 'user' ? 'items-end' : 'items-start'
              }`}
            >
              <div className="flex items-center space-x-2">
                {msg.sender === 'bot' && (
                  <FaRobot className="text-xl mt-1 text-purple-500" />
                )}
                <div
                  className={`rounded-2xl px-4 py-2 max-w-xs whitespace-pre-line ${
                    msg.sender === 'user'
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-200 text-gray-900'
                  }`}
                >
                  {msg.text}
                </div>
                {msg.sender === 'user' && (
                  <FaUser className="text-xl mt-1 text-blue-600" />
                )}
              </div>
              <div className="text-xs text-gray-400 mt-1 ml-8">
                {msg.time}
              </div>
            </motion.div>
          ))}

          {isThinking && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="flex items-center space-x-2 text-gray-600 animate-pulse"
            >
              <FaRobot className="text-xl text-purple-400" />
              <div className="bg-gray-300 rounded-full w-2 h-2 animate-bounce" />
              <div className="bg-gray-300 rounded-full w-2 h-2 animate-bounce delay-100" />
              <div className="bg-gray-300 rounded-full w-2 h-2 animate-bounce delay-200" />
              <span className="ml-2 text-sm">Bot is typing…</span>
            </motion.div>
          )}

          <div ref={bottomRef} />
        </div>

        {/* Input area with upload */}
        <div className="p-4 border-t border-gray-300 bg-white flex items-center space-x-2">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Ask something..."
            className="flex-1 px-4 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-pink-400"
          />
          <input
            type="file"
            onChange={handleFileUpload}
            className="hidden"
            id="upload-file"
          />
          <label
            htmlFor="upload-file"
            className="px-4 py-2 bg-purple-500 text-white rounded-xl cursor-pointer hover:bg-purple-600 transition"
          >
            {uploadedFileName ? `📄 ${uploadedFileName}` : 'Upload'}
          </label>
          <button
            onClick={handleSend}
            className="px-4 py-2 bg-pink-500 text-white rounded-xl hover:bg-pink-600 transition"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
