import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import "./index.css";
import logo from "./ocp.png"; // Place your logo image in the src/ folder

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [botThinking, setBotThinking] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const messagesEndRef = useRef(null);

  // Scroll to bottom on new message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    setMessages((prev) => [
      ...prev,
      { type: "user", text: input, timestamp: new Date().toLocaleTimeString() },
    ]);
    setInput("");
    setBotThinking(true);

    try {
      const response = await axios.post("http://localhost:8000/chat", {
        message: input,
      });
      const botReply = response.data.answer;

      setMessages((prev) => [
        ...prev,
        { type: "bot", text: botReply, timestamp: new Date().toLocaleTimeString() },
      ]);
    } catch (error) {
      console.error("Error sending message:", error);
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: "âŒ Error: Could not get response from the server." },
      ]);
    } finally {
      setBotThinking(false);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setSelectedFile(file);
    setIsUploading(true);
    setBotThinking(false);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/analyze-document", formData);
      const result = response.data.answer;

      setMessages((prev) => [
        ...prev,
        { type: "user", text: `ðŸ“Ž Uploaded: ${file.name}` },
        { type: "bot", text: result, timestamp: new Date().toLocaleTimeString() },
      ]);
    } catch (error) {
      console.error("File upload error:", error);
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: "âŒ Failed to upload or analyze the document." },
      ]);
    } finally {
      setIsUploading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-200 via-blue-100 to-green-100 flex flex-col items-center p-6">
      <div className="flex items-center mb-6">
        <img src={logo} alt="Chat Logo" className="w-10 h-10 mr-3" />
        <h1 className="text-3xl font-bold text-blue-900">Welcome to Open Chat</h1>
      </div>

      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-xl flex flex-col flex-grow p-6 space-y-4 overflow-auto max-h-[70vh]">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`flex flex-col ${
              msg.type === "user" ? "items-end" : "items-start"
            }`}
          >
            <div
              className={`px-4 py-2 rounded-xl text-sm max-w-xs ${
                msg.type === "user"
                  ? "bg-blue-500 text-white rounded-br-none"
                  : "bg-gray-200 text-gray-800 rounded-bl-none"
              }`}
            >
              {msg.text}
            </div>
            <span className="text-xs text-gray-400 mt-1">
              {msg.timestamp || ""}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Typing / Uploading Indicator */}
      {isUploading ? (
        <div className="text-sm text-gray-700 animate-pulse mt-2">ðŸ“¤ Uploading...</div>
      ) : botThinking ? (
        <div className="text-sm text-gray-700 animate-pulse mt-2">ðŸ¤– Bot is typing...</div>
      ) : null}

      {/* Input and Upload Controls */}
      <div className="w-full max-w-2xl flex items-center mt-4 space-x-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type a message..."
          className="flex-grow px-4 py-2 rounded-lg border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <button
          onClick={sendMessage}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition"
        >
          Send
        </button>
        <label className="bg-green-500 text-white px-4 py-2 rounded-lg cursor-pointer hover:bg-green-600 transition">
          {selectedFile ? selectedFile.name : "Upload"}
          <input type="file" hidden onChange={handleFileUpload} />
        </label>
      </div>
    </div>
  );
}

export default App;
