import React, { useState } from 'react';
import axios from 'axios';
import { FaUser, FaRobot } from 'react-icons/fa';
import { motion } from 'framer-motion';

function App() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:8000/ask', { query });
      setResponse(res.data.response);
    } catch (err) {
      console.error('Request failed:', err);
      setResponse('‚ùå Error connecting to backend.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-red-200 via-white to-red-100 flex items-center justify-center">
      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-xl p-6 flex flex-col space-y-4">
        <h1 className="text-3xl font-bold text-center text-red-600 mb-2">
          üß† Agentic AI Chat
        </h1>

        <form onSubmit={handleSubmit} className="flex space-x-2 w-full">
          <input
            type="text"
            className="flex-1 p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-red-300"
            placeholder="Ask something (e.g., What is 10 plus 5?)"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button
            type="submit"
            className="bg-red-600 text-white px-5 py-3 rounded-lg hover:bg-red-700 transition"
          >
            {loading ? 'Thinking...' : 'Send'}
          </button>
        </form>

        {response && (
          <motion.div
            initial={{ opacity: 0, y: 8 }}
            animate={{ opacity: 1, y: 0 }}
            className="flex items-start space-x-4 mt-4"
          >
            <FaRobot className="text-2xl text-red-600 mt-1" />
            <div className="bg-red-50 border border-red-200 p-4 rounded-xl shadow text-gray-800 max-w-xl">
              {response}
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
}

export default App;
